import './App.css';
import {React, useState, useEffect} from "react"
function App() {
    const [message , setMessage]=useState('')
    const [is_first_time , setIsFirstTime]=useState(true)
  const [messages , setMessages]=useState([])
useEffect(()=>{
    if (is_first_time){
        fetch('http://127.0.0.1:3012/messages')
            .then(response => response.json())
            .then(json => {
                let mess = []
                Object.entries(json.messages).forEach(([key, value]) =>   mess.push({key,value}))
                console.log(mess)
                setMessages(mess)
            })
    setIsFirstTime(false)
    }
    else{
        setInterval( ()=>{
            fetch('http://127.0.0.1:3012/messages')
                .then(response => response.json())
                .then(json => {
                    let mess = []
                    Object.entries(json.messages).forEach(([key, value]) =>   mess.push({key,value}))
                    console.log(mess)
                    setMessages(mess)
                })
        },1000)
    }
},[is_first_time])
  return (
<>
  <input type="text" value={message} onChange={(e)=>setMessage(e.target.value)}/>
    <ul>
        {
            messages.map( (message) =>  {
                let msg = ""
                Object.entries(message).forEach(([key, value]) => msg=value )
           return     <li >{msg}</li> })
        }

    </ul>
</>
  );
}

export default App;
