import './App.css';
import {React, useState, useEffect} from "react"
function App() {
    const [message , setMessage]=useState('')
    // const [is_first_time , setIsFirstTime]=useState(true)
    const [messages , setMessages]=useState([])
    // const [counter , setCounter]=useState(0)

    // solve set interval inside use effect
useEffect(()=>{
    let allmessages=[];
       const timer = window.setInterval( ()=>{
               console.log(mess)
            let lastTime =0;
            if (allmessages.length > 0)
            {
                lastTime = ((allmessages[allmessages.length-1])["key"])
            }
            // console.log(lastTime)
            // console.log(allmessages)
            fetch('http://127.0.0.1:3012/messages/'+lastTime)
                .then(response => response.json())
                .then(json => {
                    // console.log(json)
                    let mess = []
                    Object.entries(json.messages).forEach(([key, value]) =>   mess.push({key,value}))
                    // allmessages=allmessages.concat(mess)
                    setMessages(messages.concat(mess))
                })
        },
            1000)
    return () => {
        window.clearInterval(timer);
    };
    // eslint-disable-next-line
},[])
    const saveMessage = (e)=>{
        if(e.keyCode == 13){
            fetch('http://127.0.0.1:3012/messages', {
            method: 'POST',
            body: JSON.stringify({
                message: message,
            }),
            headers: {
                'Content-type': 'application/json; charset=UTF-8',
            },
        })
            .then((response) => response.json())
            .then((json) => setMessage(""));
    }}
  return (
<>
  <input type="text" value={message} onChange={(e)=>setMessage(e.target.value)} onKeyUp={saveMessage}/>
    <ul>
        {
            messages.map( (message) =>  {
                let msg = ""
                let msgKey = ""
                Object.entries(message).forEach(([key, value]) =>{
                    msg=value
                    // console.log(key)
                } )
           return     <li key={msg} >{msg}</li> })
        }
    </ul>
</>
  );
}
export default App;
