            {
            // lastTime = Object.entries(messages[messages.length-1]).forEach(([key, value]) =>  lastTime=key )
            }
            else
                    lastTime=0
                .then(response => response.json())
                .then(json => {
                    let mess = []
                    Object.entries(json.messages).forEach(([key, value]) =>   mess.push({key,value}))
                    setMessages(messages.concat(mess))
                    setCounter(counter+1)
                })
        },5000)

