        if(e.keyCode == 13){

            fetch('http://127.0.0.1:3012/messages', {
            method: 'POST',
            body: JSON.stringify({
                message: message,
            }),
            headers: {
                'Content-type': 'application/json; charset=UTF-8',
            },
        })
            .then((response) => response.json())
            .then((json) => setMessage(""));
    }}
  return (
<>
