import './App.css';
import {React, useState, useEffect} from "react"
import { io } from "socket.io-client";
import { Widget,addResponseMessage } from 'react-chat-widget';
import 'react-chat-widget/lib/styles.css';

const ENDPOINT = "http://127.0.0.1:5002"
function App() {
    const [room , setRoom]=useState('')
    const socket = io(ENDPOINT);

    // solve set interval inside use effect
    useEffect(() => {
        socket.on("messageFromServer", data => {
            addResponseMessage(data)
        });
         return ( ()=>{
            });
        },
        []
    );
    const sendMessageToRoom = (newMessage) => {
        socket.emit('messageToUser',[newMessage,room])
    };
    const joinRoom = (e)=>{
        if(e.keyCode == 13){
            socket.emit('joinRoom',room)
        }}

  return (
<>
    <Widget handleNewUserMessage={sendMessageToRoom} />

    <input type="text" value={room} onChange={(e)=>joinRoom(e.target.value)} />
    <h1>Your room : {room}</h1>


    </>
  );
}
export default App;
